import styled from 'styled-components';

export const Container = styled.div`
  min-height: 90vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 22px;
  background-color: #f8f9fa;
`;

export const Title = styled.h1`
  font-size: 44px;
  font-weight: bold;
  margin-bottom: 10px;
  color: #333;
`;
