import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import {
  deleteMovie,
  setSearchTitle,
  setSearchActor,
} from '../redux/MovieSlice';

const MovieList = () => {
  const dispatch = useDispatch();
  const movies = useSelector(state => state.movies.movies);
  const searchTitle = useSelector(state => state.movies.searchTitle);
  const searchActor = useSelector(state => state.movies.searchActor);

  const handleDelete = id => {
    dispatch(deleteMovie(id));
  };

  // Фільтруємо за назвою (частковий пошук, регістро-незалежний)
  const filteredByTitle = movies.filter(movie =>
    movie.title.toLowerCase().includes(searchTitle.toLowerCase())
  );

  // Фільтруємо за актором (перевірка, чи є хоч один актор, що містить рядок пошуку)
  const filteredByActor = filteredByTitle.filter(movie =>
    movie.stars.some(actor =>
      actor.toLowerCase().includes(searchActor.toLowerCase())
    )
  );

  // Сортуємо остаточний список за назвою
  const sortedMovies = filteredByActor.sort((a, b) =>
    a.title.localeCompare(b.title)
  );

  return (
    <div>
      <h2 className="text-xl font-semibold mt-4 mb-2">Movie List</h2>

      <div className="mb-2 space-x-2">
        <input
          type="text"
          placeholder="Search by title"
          value={searchTitle}
          onChange={e => dispatch(setSearchTitle(e.target.value))}
          className="border p-1"
        />
        <input
          type="text"
          placeholder="Search by actor"
          value={searchActor}
          onChange={e => dispatch(setSearchActor(e.target.value))}
          className="border p-1"
        />
      </div>

      <ul className="space-y-2">
        {sortedMovies.length === 0 ? (
          <li>No movies found.</li>
        ) : (
          sortedMovies.map(movie => (
            <li key={movie.id} className="border p-2 rounded">
              <strong>{movie.title}</strong> ({movie.releaseYear}) [
              {movie.format}]
              <br />
              Stars: {movie.stars.join(', ')}
              <button
                onClick={() => handleDelete(movie.id)}
                className="ml-4 text-red-500 hover:underline"
              >
                Delete
              </button>
            </li>
          ))
        )}
      </ul>
    </div>
  );
};

export default MovieList;
